import Vue from 'vue'
import VueRouter from 'vue-router'
import routes from './routes'
Vue.use(VueRouter);
const token = true
const router = new VueRouter({
    routes
})
// 路由前置守卫
router.beforeEach((to,from,next)=>{
    console.log('页面跳转从', from.path,'到',to.path);
    if(token){
        next()
    }else{
        console.log('不好意思没有token');
    }

})
export default router;
